package com.ldxx.utils;import java.util.regex.Matcher;import java.util.regex.Pattern;public class StringUtils {    /**     *     * @param str     * @return     */    public static boolean isEmpty(String str) {        if (null == str) {            return true;        }        str = str.trim();        return !(str.length() > 0 && !"null".equals(str));    }    /**     *     * @param num     * @return     */    public static String getFullBallNum(String num) {        if (isEmpty(num)) {            return "";        }        else if (num.length() > 1) {            return num;        }        else {            int i = Integer.parseInt(num);            if (i < 10) {                return "0" + i;            }            else {                return num;            }        }    }    /**     *     * @param num     * @return     */    public static boolean isZNumber(String num) {        if (num == null) {            return false;        }        Pattern p = Pattern.compile("^\\d*[1-9]\\d*$");        Matcher m = p.matcher(num);        return m.matches();    }    /**     *     */    public static String getMethodNameByAttr(String setOrGet, String attr) {        attr = attr.toLowerCase();        StringBuffer sb = new StringBuffer(setOrGet);        sb.append(attr.substring(0, 1).toUpperCase()).append(attr.substring(1));        return sb.toString();    }    public static boolean isEmptyTrimmed(String str) {        return str == null || str.toString().trim().isEmpty();    }    /**     *     * @param str     * @param separator     * @return     */    public static String getString(String[] str,String separator){        if (str == null) {            return "";        }        StringBuffer sb = new StringBuffer();        for (String s : str) {            sb.append(s).append(separator);        }        return sb.toString();    }    /**     *     * @param str     * @return     */    public static String getString(String[] str){        return getString(str," ");    }}